import React, { useState, useRef } from 'react';
import Canvas from './components/canvas/Canvas';
import LayerLibrary from './components/sidebar/LayerLibrary';
import PropertiesPanel from './components/sidebar/PropertiesPanel';
import Toolbar from './components/controls/Toolbar';

const App: React.FC = () => {
    // Add state to track export status
    const [exportStatus, setExportStatus] = useState<{
        isExporting: boolean;
        message: string;
        isError: boolean;
    }>({
        isExporting: false,
        message: '',
        isError: false
    });

    // Reference to Canvas component to get model data
    const canvasRef = useRef<any>(null);

    // Function to handle export
    const handleExport = async () => {
        if (!canvasRef.current) {
            setExportStatus({
                isExporting: false,
                message: 'Canvas reference not available',
                isError: true
            });
            return;
        }

        try {
            setExportStatus({
                isExporting: true,
                message: 'Exporting model...',
                isError: false
            });

            // Get model data from Canvas component
            const modelData = canvasRef.current.getModelData();

            // Sanitize the data to ensure JSON compatibility
            const sanitizedData = {
                layers: modelData.layers.map((layer: any) => ({
                    id: layer.id,
                    type: layer.type,
                    parameters: { ...layer.parameters },
                    position: { x: layer.x || 0, y: layer.y || 0 }
                })),
                connections: modelData.connections.map((conn: any) => ({
                    id: conn.id || null,
                    from: conn.from,
                    to: conn.to
                }))
            };

            console.log('Sending model data:', JSON.stringify(sanitizedData));

            // Use relative URL that will work with tunneling
            const apiUrl = '/api/export-model';

            const response = await fetch(apiUrl, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(sanitizedData),
            });

            if (!response.ok) {
                throw new Error(`Server responded with ${response.status}`);
            }

            // For file download responses, handle the blob instead of parsing JSON
            const contentType = response.headers.get('content-type');
            if (contentType && contentType.includes('application/json')) {
                const result = await response.json();
                setExportStatus({
                    isExporting: false,
                    message: 'Export successful! ' + result.message,
                    isError: false
                });
            } else {
                // Handle file download - browser will automatically download the file
                setExportStatus({
                    isExporting: false,
                    message: 'Export successful! Your file is downloading...',
                    isError: false
                });
            }

        } catch (error) {
            console.error('Export failed:', error);
            setExportStatus({
                isExporting: false,
                message: `Export failed: ${error instanceof Error ? error.message : 'Unknown error'}`,
                isError: true
            });
        }
    };

    return (
        <div className="app" style={{ 
            height: '100vh', 
            display: 'flex', 
            flexDirection: 'column',
            backgroundColor: '#0d0d0dff',
            color: '#ffffff'
        }}>
            <Toolbar onExport={handleExport} />

            {/* Status message */}
            {exportStatus.message && (
                <div style={{
                    padding: '8px 16px',
                    backgroundColor: exportStatus.isError ? '#f44336' : '#4caf50',
                    color: 'white',
                    textAlign: 'center',
                    opacity: exportStatus.message ? 1 : 0,
                    transition: 'opacity 0.3s',
                }}>
                    {exportStatus.message}
                </div>
            )}

            <div className="main-container" style={{ flex: 1, display: 'flex' }}>
                <div style={{ maxHeight: '70vh', overflowY: 'auto' }}>
                    <LayerLibrary />
                </div>
                <div style={{ flex: 1 }}>
                    <Canvas ref={canvasRef} />
                </div>
                <PropertiesPanel />
            </div>
        </div>
    );
};

export default App;
