import React, { useState, useRef } from 'react';
import Canvas from './components/canvas/Canvas';
import LayerLibrary from './components/sidebar/LayerLibrary';
import PropertiesPanel from './components/sidebar/PropertiesPanel';
import Toolbar from './components/controls/Toolbar';

const App: React.FC = () => {
    // Add state to track export status
    const [exportStatus, setExportStatus] = useState<{
        isExporting: boolean;
        message: string;
        isError: boolean;
    }>({
        isExporting: false,
        message: '',
        isError: false
    });

    // Reference to Canvas component to get model data
    const canvasRef = useRef<any>(null);

    // Function to handle export
    const handleExport = async () => {
        if (!canvasRef.current) {
            setExportStatus({
                isExporting: false,
                message: 'Canvas reference not available',
                isError: true
            });
            return;
        }

        try {
            setExportStatus({
                isExporting: true,
                message: 'Exporting model...',
                isError: false
            });

            // Get model data from Canvas component
            const modelData = canvasRef.current.getModelData();

            // Use relative URL that will work with tunneling
            const apiUrl = '/api/export-model';

            const response = await fetch(apiUrl, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(modelData),
            });

            if (!response.ok) {
                throw new Error(`Server responded with ${response.status}`);
            }

            const result = await response.json();

            if (!result.success) {
                throw new Error(result.message || 'Export failed');
            }

            // Create a downloadable file from the Python code
            const blob = new Blob([result.message], { type: 'text/plain' });
            const url = URL.createObjectURL(blob);

            // Create a temporary link element to trigger download
            const a = document.createElement('a');
            a.href = url;
            a.download = 'neural_network_model.py';
            document.body.appendChild(a);
            a.click();

            // Clean up
            document.body.removeChild(a);
            URL.revokeObjectURL(url);

            setExportStatus({
                isExporting: false,
                message: 'Export successful! File downloaded.',
                isError: false
            });

            // Clear status message after 3 seconds
            setTimeout(() => {
                setExportStatus(prev => ({
                    ...prev,
                    message: ''
                }));
            }, 3000);

        } catch (error) {
            console.error('Export failed:', error);
            setExportStatus({
                isExporting: false,
                message: `Export failed: ${error instanceof Error ? error.message : 'Unknown error'}`,
                isError: true
            });
        }
    };

    return (
        <div className="app" style={{ 
            height: '100vh', 
            display: 'flex', 
            flexDirection: 'column',
            backgroundColor: '#000000ff',
            color: '#ffffff'
        }}>
            <Toolbar onExport={handleExport} />

            {/* Status message */}
            {exportStatus.message && (
                <div style={{
                    padding: '8px 16px',
                    backgroundColor: exportStatus.isError ? '#f44336' : '#4caf50',
                    color: 'white',
                    textAlign: 'center',
                    opacity: exportStatus.message ? 1 : 0,
                    transition: 'opacity 0.3s',
                }}>
                    {exportStatus.message}
                </div>
            )}

            <div className="main-container" style={{ 
                flex: 1, 
                display: 'flex', 
                overflow: 'hidden',
                height: 'calc(100vh - 80px)' // Ensure it takes remaining space
            }}>
                <LayerLibrary />
                <div style={{ 
                    flex: 1, 
                    position: 'relative', 
                    overflow: 'hidden',
                    height: '100%' // Ensure the canvas container takes full height
                }}>
                    <Canvas ref={canvasRef} />
                </div>
                <PropertiesPanel />
            </div>
        </div>
    );
};

export default App;
