import React, { useState, useRef } from 'react';
import DropZone from './DropZone';
import InputLayer from '../layers/InputLayer';
import Conv2DLayer from '../layers/Conv2DLayer';
import OutputLayer from '../layers/OutputLayer';
import ConcatLayer from '../layers/ConcatLayer';

type LayerType = 'InputLayer' | 'Conv2DLayer' | 'OutputLayer' | 'ConcatLayer';

interface LayerProps {
    [key: string]: any;
}

const layerComponentMap: Record<LayerType, React.ComponentType<LayerProps>> = {
    InputLayer: InputLayer,
    Conv2DLayer: Conv2DLayer,
    OutputLayer: OutputLayer,
    ConcatLayer: ConcatLayer,
};

const defaultParams: Record<LayerType, Record<string, any>> = {
    InputLayer: { inputShape: [28, 28, 1] },
    Conv2DLayer: { filterSize: 3, numFilters: 32, activationFunction: 'relu' },
    OutputLayer: { outputShape: [10], activationFunction: 'softmax' },
    ConcatLayer: { layers: [] },
};

interface DroppedLayer {
    type: LayerType;
    id: number;
    parameters: Record<string, any>;
    x: number;
    y: number;
    selected?: boolean;
}

interface Connection {
    from: number;
    to: number;
}

const Canvas: React.FC = () => {
    const [layers, setLayers] = useState<DroppedLayer[]>([]);
    const [connections, setConnections] = useState<Connection[]>([]);
    const [draggedId, setDraggedId] = useState<number | null>(null);
    const [dragOffset, setDragOffset] = useState<{ x: number; y: number }>({ x: 0, y: 0 });
    const [connecting, setConnecting] = useState<{ from: number | null, to: number | null }>({ from: null, to: null });
    const canvasRef = useRef<HTMLDivElement>(null);

    // Add new layer at a default position
    const handleDrop = (layer: { type: string; id: number }) => {
        // Only allow valid LayerType
        if (!['InputLayer', 'Conv2DLayer', 'OutputLayer', 'ConcatLayer'].includes(layer.type)) {
            return;
        }

        const validLayerTypes: LayerType[] = ['InputLayer', 'Conv2DLayer', 'OutputLayer', 'ConcatLayer'];
        const type = validLayerTypes.find(t => t === layer.type);

        if (!type) {
            return;
        }

        setLayers(prev => [
            ...prev,
            {
                ...layer,
                type: type,
                parameters: { ...defaultParams[type] },
                x: 100 + prev.length * 40,
                y: 60 + prev.length * 40,
            },
        ]);
    };

    // Improved drag logic
    const handleMouseDown = (id: number, e: React.MouseEvent) => {
        if (!canvasRef.current) return;

        const layer = layers.find(l => l.id === id);
        if (!layer) return;

        const rect = canvasRef.current.getBoundingClientRect();
        const mouseX = e.clientX - rect.left;
        const mouseY = e.clientY - rect.top;

        // Calculate offset from mouse position to layer's top-left corner
        const offsetX = mouseX - layer.x;
        const offsetY = mouseY - layer.y;

        setDraggedId(id);
        setDragOffset({ x: offsetX, y: offsetY });
        e.stopPropagation();
    };

    const handleMouseUp = () => {
        setDraggedId(null);
        setDragOffset({ x: 0, y: 0 });
    };

    const handleMouseMove = (e: React.MouseEvent) => {
        if (draggedId !== null && canvasRef.current) {
            const rect = canvasRef.current.getBoundingClientRect();
            const mouseX = e.clientX - rect.left;
            const mouseY = e.clientY - rect.top;

            // Apply the offset to maintain relative position
            const x = mouseX - dragOffset.x;
            const y = mouseY - dragOffset.y;

            setLayers(prev => prev.map(l => l.id === draggedId ? { ...l, x, y } : l));
        }
    };

    // Inline parameter editing with proper type conversion
    const handleParamChange = (id: number, param: string, value: string) => {
        setLayers(prev => prev.map(l => {
            if (l.id === id) {
                let convertedValue: any = value;

                // Convert numeric strings to numbers
                if (!isNaN(Number(value)) && value.trim() !== '') {
                    convertedValue = Number(value);
                }

                // Handle array inputs (for inputShape, outputShape)
                if (param.includes('Shape') && typeof value === 'string') {
                    try {
                        // Parse comma-separated values as array
                        convertedValue = value.split(',').map(v => Number(v.trim())).filter(v => !isNaN(v));
                    } catch {
                        convertedValue = value;
                    }
                }

                return { ...l, parameters: { ...l.parameters, [param]: convertedValue } };
            }
            return l;
        }));
    };

    // Connection logic with actual storage
    const handleStartConnect = (id: number) => setConnecting({ from: id, to: null });
    const handleEndConnect = (id: number) => {
        if (connecting.from !== null && connecting.from !== id) {
            // Check if connection already exists
            const connectionExists = connections.some(
                conn => conn.from === connecting.from && conn.to === id
            );

            if (!connectionExists) {
                const fromId = connecting.from;
                setConnections(prev => [...prev, { from: fromId, to: id }]);
            }

            setConnecting({ from: null, to: null });
        }
    };

    // Helper function to get layer center position for connection lines
    const getLayerCenter = (layer: DroppedLayer) => {
        return {
            x: layer.x + 60, // Half of minWidth (120)
            y: layer.y + 50  // Approximate center height
        };
    };

    // Render connection lines
    const renderConnections = () => {
        return connections.map((connection, index) => {
            const fromLayer = layers.find(l => l.id === connection.from);
            const toLayer = layers.find(l => l.id === connection.to);

            if (!fromLayer || !toLayer) return null;

            const from = getLayerCenter(fromLayer);
            const to = getLayerCenter(toLayer);

            return (
                <svg
                    key={index}
                    style={{
                        position: 'absolute',
                        top: 0,
                        left: 0,
                        width: '100%',
                        height: '100%',
                        pointerEvents: 'none',
                        zIndex: 0
                    }}
                >
                    <line
                        x1={from.x}
                        y1={from.y}
                        x2={to.x}
                        y2={to.y}
                        stroke="#007acc"
                        strokeWidth="2"
                        markerEnd="url(#arrowhead)"
                    />
                    <defs>
                        <marker
                            id="arrowhead"
                            markerWidth="10"
                            markerHeight="7"
                            refX="9"
                            refY="3.5"
                            orient="auto"
                        >
                            <polygon
                                points="0 0, 10 3.5, 0 7"
                                fill="#007acc"
                            />
                        </marker>
                    </defs>
                </svg>
            );
        });
    };

    return (
        <div
            className="canvas"
            ref={canvasRef}
            style={{ minHeight: 500, border: '1px solid #ccc', position: 'relative', overflow: 'hidden' }}
            onMouseMove={handleMouseMove}
            onMouseUp={handleMouseUp}
        >
            <DropZone onDrop={handleDrop} />

            {/* Render connection lines */}
            {renderConnections()}

            {/* Render blocks */}
            {layers.map((layer) => {
                const LayerComponent = layerComponentMap[layer.type];
                if (!LayerComponent) return null;

                // Inline parameter editing UI for demo
                const paramInputs = Object.keys(layer.parameters).map(param => {
                    const value = layer.parameters[param];
                    const displayValue = Array.isArray(value) ? value.join(', ') : String(value);

                    return (
                        <div key={param} style={{ marginBottom: 4 }}>
                            <label style={{ fontSize: 12 }}>{param}: </label>
                            <input
                                style={{ fontSize: 12, width: 80 }}
                                value={displayValue}
                                onChange={e => handleParamChange(layer.id, param, e.target.value)}
                                placeholder={param.includes('Shape') ? "e.g. 28, 28, 1" : ""}
                            />
                        </div>
                    );
                });

                return (
                    <div
                        key={layer.id}
                        style={{
                            position: 'absolute',
                            left: layer.x,
                            top: layer.y,
                            background: connecting.from === layer.id ? '#e6f3ff' : '#f9f9f9',
                            border: connecting.from === layer.id ? '2px solid #007acc' : '2px solid #aaa',
                            borderRadius: 8,
                            padding: 12,
                            minWidth: 120,
                            cursor: 'move',
                            zIndex: draggedId === layer.id ? 2 : 1,
                        }}
                        onMouseDown={e => handleMouseDown(layer.id, e)}
                        onDoubleClick={() => handleStartConnect(layer.id)}
                        onMouseUp={() => handleEndConnect(layer.id)}
                    >
                        <LayerComponent {...layer.parameters} />
                        <div style={{ marginTop: 8 }}>{paramInputs}</div>
                        <div style={{ fontSize: 10, color: '#888', marginTop: 4 }}>
                            Double-click to start connection, then click another block
                        </div>

                        {/* Connection indicators */}
                        <div style={{ 
                            position: 'absolute', 
                            top: -8, 
                            right: -8, 
                            width: 16, 
                            height: 16, 
                            background: '#007acc', 
                            borderRadius: '50%',
                            display: connecting.from === layer.id ? 'block' : 'none'
                        }} />
                    </div>
                );
            })}
        </div>
    );
};

export default Canvas;
