import React from 'react';

const LayerLibrary: React.FC = () => {
    const layers = [
        { type: 'InputLayer', name: 'Input Layer', icon: '', color: '#315c4aff' },
        { type: 'Conv2DLayer', name: 'Conv2D Layer', icon: '', color: '#315c4aff' },
        { type: 'MaxPoolLayer', name: 'MaxPool Layer', icon: '', color: '#315c4aff' },
        { type: 'FlattenLayer', name: 'Flatten Layer', icon: '', color: '#315c4aff' },
        { type: 'LinearLayer', name: 'Linear Layer', icon: '', color: '#315c4aff' },
        { type: 'OutputLayer', name: 'Output Layer', icon: '', color: '#315c4aff' },
        { type: 'ConcatLayer', name: 'Concat Layer', icon: '', color: '#315c4aff' },
    ];

    const handleDragStart = (e: React.DragEvent, layerType: string) => {
        const layerData = {
            type: layerType,
            id: Date.now() + Math.random()
        };
        e.dataTransfer.setData('text/plain', JSON.stringify(layerData));
    };

    return (
        <div style={{
            width: '250px',
            backgroundColor: '#000000ff',
            padding: '16px',
            borderRight: '0px solid #000000ff',
            height: 'calc(100vh - 80px)', // Adjust to match viewport height minus header
            overflowY: 'auto'
        }}>
            <h3 style={{ 
                color: '#ffffff', 
                marginTop: 0, 
                marginBottom: '16px',
                fontSize: '18px'
            }}>
                Layer Library
            </h3>

            <div style={{ display: 'flex', flexDirection: 'column', gap: '8px' }}>
                {layers.map((layer) => (
                    <div
                        key={layer.type}
                        draggable
                        onDragStart={(e) => handleDragStart(e, layer.type)}
                        style={{
                            padding: '12px',
                            backgroundColor: '#151515ff',
                            border: `2px solid ${layer.color}`,
                            borderRadius: '8px',
                            cursor: 'grab',
                            transition: 'all 0.2s ease',
                            display: 'flex',
                            alignItems: 'center',
                            gap: '12px'
                        }}
                        onMouseDown={(e) => e.currentTarget.style.cursor = 'grabbing'}
                        onMouseUp={(e) => e.currentTarget.style.cursor = 'grab'}
                        onMouseOver={(e) => {
                            e.currentTarget.style.backgroundColor = '#151515ff';
                            e.currentTarget.style.transform = 'translateY(-2px)';
                        }}
                        onMouseOut={(e) => {
                            e.currentTarget.style.backgroundColor = '#000000ff';
                            e.currentTarget.style.transform = 'translateY(0)';
                        }}
                    >
                        <span style={{ fontSize: '20px' }}>{layer.icon}</span>
                        <span style={{ 
                            color: '#ffffff', 
                            fontWeight: '500',
                            fontSize: '14px'
                        }}>
                            {layer.name}
                        </span>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default LayerLibrary;
