import React, { useState } from 'react';

interface Conv2DLayerProps {
    filterSize?: number;
    numFilters?: number;
    activationFunction?: string;
}

const Conv2DLayer: React.FC<Conv2DLayerProps> = ({ 
    filterSize = 3, 
    numFilters = 32, 
    activationFunction = 'relu' 
}) => {
    const [localFilterSize, setLocalFilterSize] = useState(filterSize);
    const [localNumFilters, setLocalNumFilters] = useState(numFilters);
    const [localActivationFunction, setLocalActivationFunction] = useState(activationFunction);

    const handleFilterSizeChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        setLocalFilterSize(Number(event.target.value));
    };

    const handleNumFiltersChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        setLocalNumFilters(Number(event.target.value));
    };

    const handleActivationFunctionChange = (event: React.ChangeEvent<HTMLSelectElement>) => {
        setLocalActivationFunction(event.target.value);
    };

    return (
        <div className="conv2d-layer" style={{ userSelect: 'none' }}>
            <h3 style={{ color: '#ffffff', margin: 0, fontSize: 14, userSelect: 'none' }}>Conv2D Layer</h3>
            <div style={{ marginTop: 8, userSelect: 'none' }}>
                <label style={{ color: '#ccc', fontSize: 12, userSelect: 'none' }}>Filter Size:</label>
                <input 
                    type="number" 
                    value={localFilterSize} 
                    onChange={handleFilterSizeChange}
                    style={{
                        backgroundColor: '#3a3a3a',
                        border: '1px solid #555',
                        color: '#ffffff',
                        padding: '2px 4px',
                        borderRadius: '2px',
                        width: '60px',
                        marginLeft: '4px'
                    }}
                />
            </div>
            <div style={{ marginTop: 4, userSelect: 'none' }}>
                <label style={{ color: '#ccc', fontSize: 12, userSelect: 'none' }}>Number of Filters:</label>
                <input 
                    type="number" 
                    value={localNumFilters} 
                    onChange={handleNumFiltersChange}
                    style={{
                        backgroundColor: '#3a3a3a',
                        border: '1px solid #555',
                        color: '#ffffff',
                        padding: '2px 4px',
                        borderRadius: '2px',
                        width: '60px',
                        marginLeft: '4px'
                    }}
                />
            </div>
            <div style={{ marginTop: 4, userSelect: 'none' }}>
                <label style={{ color: '#ccc', fontSize: 12, userSelect: 'none' }}>Activation Function:</label>
                <select 
                    value={localActivationFunction} 
                    onChange={handleActivationFunctionChange}
                    style={{
                        backgroundColor: '#3a3a3a',
                        border: '1px solid #555',
                        color: '#ffffff',
                        padding: '2px 4px',
                        borderRadius: '2px',
                        marginLeft: '4px'
                    }}
                >
                    <option value="relu">ReLU</option>
                    <option value="sigmoid">Sigmoid</option>
                    <option value="tanh">Tanh</option>
                </select>
            </div>
            <div style={{ fontSize: 12, color: '#888', marginTop: 8, userSelect: 'none' }}>
                <div>Filters: {localNumFilters}</div>
                <div>Size: {localFilterSize}x{localFilterSize}</div>
                <div>Activation: {localActivationFunction}</div>
            </div>
        </div>
    );
};

export default Conv2DLayer;
