import React, { useState, useRef } from 'react';
import Canvas from './components/canvas/Canvas';
import LayerLibrary from './components/sidebar/LayerLibrary';
import PropertiesPanel from './components/sidebar/PropertiesPanel';
import Toolbar from './components/controls/Toolbar';

interface TrainingOptions {
    batchSize: number;
    epochs: number;
    learningRate: number;
    optimizer: string;
    lossFunction: string;
}

const App: React.FC = () => {
    // Add state to track export status
    const [exportStatus, setExportStatus] = useState<{
        isExporting: boolean;
        message: string;
        isError: boolean;
    }>({
        isExporting: false,
        message: '',
        isError: false
    });

    // State for training options
    const [trainingOptions, setTrainingOptions] = useState<TrainingOptions>({
        batchSize: 32,
        epochs: 10,
        learningRate: 0.001,
        optimizer: 'adam',
        lossFunction: 'mse',
    });

    // Handler to update training options
    const handleOptionChange = (option: string, value: any) => {
        setTrainingOptions(prev => ({ ...prev, [option]: value }));
    };

    // Reference to Canvas component to get model data
    const canvasRef = useRef<any>(null);

    // Function to handle export
    const handleExport = async () => {
        if (!canvasRef.current) {
            setExportStatus({
                isExporting: false,
                message: 'Canvas reference not available',
                isError: true
            });
            return;
        }

        try {
            setExportStatus({
                isExporting: true,
                message: 'Exporting model...',
                isError: false
            });

            // Get model data from Canvas component
            const modelData = canvasRef.current.getModelData();

            // Combine model data with training options
            const exportData = {
                ...modelData,
                trainingOptions: trainingOptions
            };

            // Build endpoint list safely for tunnel/https
            const isHttps = typeof window !== 'undefined' && window.location.protocol === 'https:';
            const apiBase = (process.env.REACT_APP_API_BASE || '').trim().replace(/\/+$/, '');
            const sameOrigin = (typeof window !== 'undefined' ? window.location.origin : '').replace(/\/+$/, '');

            const candidates = [
                // Same-origin paths (works in CRA dev via setupProxy)
                `${sameOrigin}/api/export-model`,
                `${sameOrigin}/export-model`,
                // Explicit API base if provided
                apiBase ? `${apiBase}/api/export-model` : '',
                apiBase ? `${apiBase}/export-model` : '',
                // Last resort: localhost (only if not on HTTPS to avoid mixed content)
                !isHttps ? 'http://localhost:5000/api/export-model' : ''
            ].filter(Boolean);

            // De-duplicate while preserving order
            const seen = new Set<string>();
            const endpoints = candidates.filter(u => (seen.has(u) ? false : (seen.add(u), true)));

            console.log('Trying endpoints:', endpoints);

            let result: any = null;
            let lastError: any = null;
            let anyRequestSent = false;

            for (const url of endpoints) {
                try {
                    console.log(`Attempting to fetch from: ${url}`);
                    anyRequestSent = true;

                    const fetchPromise = fetch(url, {
                        method: 'POST',
                        headers: { 'Content-Type': 'application/json' },
                        body: JSON.stringify(exportData),
                    });

                    // Add timeout to each fetch request
                    const timeoutPromise = new Promise((_, reject) => 
                        setTimeout(() => reject(new Error(`Request to ${url} timed out after 15s`)), 15000)
                    );

                    // Race the fetch against the timeout
                    const response = await Promise.race([fetchPromise, timeoutPromise]) as Response;

                    const contentType = response.headers.get('content-type') || '';
                    const isJson = contentType.includes('application/json');
                    const payload = isJson ? await response.json() : await response.text();

                    if (!response.ok) {
                        const msg = typeof payload === 'string' ? payload.slice(0, 200) : JSON.stringify(payload);
                        throw new Error(`HTTP ${response.status} from ${url} - ${msg}`);
                    }

                    if (!isJson) {
                        // Likely got HTML (e.g., static host), try next candidate
                        throw new Error(`Non-JSON response from ${url}: ${String(payload).slice(0, 120)}`);
                    }

                    result = payload;
                    break;
                } catch (err) {
                    console.error(`Error fetching from ${url}:`, err);
                    lastError = err;
                    continue;
                }
            }

            if (!result) {
                if (!anyRequestSent) {
                    throw new Error('No API endpoints were available to process your export request');
                }
                throw lastError || new Error('Export failed: no valid response from any endpoint');
            }

            if (!result.success) {
                throw new Error(result.message || 'Export failed');
            }

            // Create a downloadable file from the Python code
            const blob = new Blob([result.message], { type: 'text/plain' });
            const url = URL.createObjectURL(blob);

            // Create a temporary link element to trigger download
            const a = document.createElement('a');
            a.href = url;
            a.download = 'autai_model.py';
            document.body.appendChild(a);
            a.click();

            // Clean up
            document.body.removeChild(a);
            URL.revokeObjectURL(url);

            setExportStatus({
                isExporting: false,
                message: 'Export successful! File downloaded.',
                isError: false
            });

            // Clear status message after 3 seconds
            setTimeout(() => {
                setExportStatus(prev => ({
                    ...prev,
                    message: ''
                }));
            }, 3000);

        } catch (error) {
            console.error('Export failed:', error);

            // Offer JSON fallback when API fails
            const fallbackExport = () => {
                try {
                    const modelData = canvasRef.current?.getModelData();
                    const exportData = {
                        ...modelData,
                        trainingOptions: trainingOptions
                    };

                    const jsonStr = JSON.stringify(exportData, null, 2);
                    const blob = new Blob([jsonStr], { type: 'application/json' });
                    const url = URL.createObjectURL(blob);

                    const a = document.createElement('a');
                    a.href = url;
                    a.download = 'autai_model.json';
                    document.body.appendChild(a);
                    a.click();

                    document.body.removeChild(a);
                    URL.revokeObjectURL(url);

                    setExportStatus({
                        isExporting: false,
                        message: 'Model exported as JSON (API connection failed)',
                        isError: true
                    });
                } catch (e) {
                    console.error('Even fallback export failed:', e);
                    setExportStatus({
                        isExporting: false,
                        message: `Complete export failure: ${e instanceof Error ? e.message : 'Unknown error'}`,
                        isError: true
                    });
                }
            };

            setExportStatus({
                isExporting: false,
                message: `Export failed: ${error instanceof Error ? error.message : 'Unknown error'} | ` +
                         `<button style="background:#2a2a38;color:white;border:1px solid #555;padding:3px 8px;border-radius:4px;cursor:pointer" ` +
                         `onclick="window._exportFallback()">Download JSON instead</button>`,
                isError: true
            });

            // Expose the fallback function globally so the button can call it
            (window as any)._exportFallback = fallbackExport;
        }
    };

    return (
        <div className="app" style={{ 
            height: '100vh', 
            display: 'flex', 
            flexDirection: 'column',
            background: 'linear-gradient(135deg, #0f0f15 0%, #151520 100%)',
            color: '#ffffff'
        }}>
            <Toolbar onExport={handleExport} />

            {/* Status message */}
            {exportStatus.message && (
                <div 
                    style={{
                        padding: '10px 16px',
                        background: exportStatus.isError 
                            ? 'linear-gradient(90deg, #ff416c 0%, #ff4b2b 100%)' 
                            : 'linear-gradient(90deg, #00b09b 0%, #96c93d 100%)',
                        color: 'white',
                        textAlign: 'center',
                        opacity: exportStatus.message ? 1 : 0,
                        transition: 'opacity 0.3s',
                        fontWeight: '500',
                        boxShadow: '0 2px 10px rgba(0, 0, 0, 0.15)'
                    }}
                    dangerouslySetInnerHTML={{ __html: exportStatus.message }}
                />
            )}

            <div className="main-container" style={{ 
                flex: 1, 
                display: 'flex', 
                overflow: 'hidden',
                height: 'calc(100vh - 80px)'
            }}>
                <LayerLibrary />
                <div style={{ 
                    flex: 1, 
                    position: 'relative', 
                    overflow: 'hidden',
                    height: '100%'
                }}>
                    <Canvas ref={canvasRef} />
                </div>
                <PropertiesPanel 
                    trainingOptions={trainingOptions}
                    onOptionChange={handleOptionChange}
                />
            </div>
        </div>
    );
};

export default App;
