import React from 'react';

interface DropZoneProps {
    onDrop: (layer: { type: string; id: number }) => void;
}

const DropZone: React.FC<DropZoneProps> = ({ onDrop }) => {
    const [isOver, setIsOver] = React.useState(false);

    const handleDragOver = (e: React.DragEvent<HTMLDivElement>) => {
        e.preventDefault();
        setIsOver(true);
    };
    const handleDragLeave = () => setIsOver(false);
    const handleDrop = (e: React.DragEvent<HTMLDivElement>) => {
        e.preventDefault();
        setIsOver(false);
        const type = e.dataTransfer.getData('layerType');
        if (type) {
            onDrop({ type, id: Date.now() });
        }
    };

    return (
        <div
            onDragOver={handleDragOver}
            onDragLeave={handleDragLeave}
            onDrop={handleDrop}
            style={{ border: isOver ? '2px dashed green' : '2px dashed gray', height: 40, textAlign: 'center', lineHeight: '40px', background: isOver ? '#eaffea' : '#fafafa' }}
        >
            {isOver ? 'Release to drop' : 'Drag a layer here'}
        </div>
    );
};

export default DropZone;
