import React from 'react';

abstract class BaseLayer extends React.Component {
    abstract renderLayer(): JSX.Element;

    // Common properties for all layers
    protected layerName: string;
    protected parameters: Record<string, any>;

    constructor(layerName: string, parameters: Record<string, any>) {
        super({});
        this.layerName = layerName;
        this.parameters = parameters;
    }

    // Method to update parameters
    updateParameters(newParameters: Record<string, any>) {
        this.parameters = { ...this.parameters, ...newParameters };
        this.forceUpdate(); // Re-render the component
    }

    render() {
        return (
            <div className="layer">
                <h3>{this.layerName}</h3>
                {this.renderLayer()}
            </div>
        );
    }
}

export default BaseLayer;
