import React, { useState } from 'react';

interface TrainingConfig {
    batchSize: number;
    epochs: number;
    learningRate: number;
    optimizer: string;
    lossFunction: string;
    validationSplit: number;
}

interface PropertiesPanelProps {
    initialConfig: TrainingConfig;
    onConfigUpdate: (config: TrainingConfig) => void;
}

const PropertiesPanel: React.FC<PropertiesPanelProps> = ({ 
    initialConfig,
    onConfigUpdate 
}) => {
    const [trainingConfig, setTrainingConfig] = useState<TrainingConfig>(initialConfig);

    const handleConfigChange = (key: keyof TrainingConfig, value: string | number) => {
        const newConfig = { ...trainingConfig, [key]: value };
        setTrainingConfig(newConfig);
    };

    const handleApply = () => {
        onConfigUpdate(trainingConfig);
        const successEl = document.getElementById('save-success');
        if (successEl) {
            successEl.style.opacity = '1';
            setTimeout(() => {
                successEl.style.opacity = '0';
            }, 2000);
        }
    };

    return (
        <div style={{ 
            width: 200, 
            borderLeft: '1px solid rgba(255, 255, 255, 0.1)', 
            padding: 16, 
            background: 'linear-gradient(135deg, #0f0f15 0%, #151520 100%)',
            color: '#ffffff',
            height: 'calc(100vh - 80px)',
            overflowY: 'auto',
            boxShadow: '-2px 0 15px rgba(0, 0, 0, 0.2)'
        }}>
            <h3 style={{ 
                color: '#ffffff', 
                marginTop: 0,
                borderBottom: '1px solid rgba(255, 255, 255, 0.1)',
                paddingBottom: '10px',
                fontWeight: 600,
                letterSpacing: '0.5px'
            }}>Training Options</h3>

            <div style={{
                marginTop: '12px',
                display: 'flex',
                flexDirection: 'column',
                gap: '16px'
            }}>
                {/* Batch Size */}
                <div className="training-option">
                    <label style={{
                        display: 'block',
                        marginBottom: '6px',
                        color: '#b0b8c4',
                        fontSize: '14px',
                        fontWeight: 500
                    }}>Batch Size</label>
                    <input
                        type="number"
                        value={trainingConfig.batchSize}
                        onChange={(e) => handleConfigChange('batchSize', Number(e.target.value))}
                        min="1"
                        style={{
                            width: '100%',
                            padding: '8px 10px',
                            background: 'rgba(255, 255, 255, 0.07)',
                            border: '1px solid rgba(255, 255, 255, 0.1)',
                            borderRadius: '8px',
                            color: 'white',
                            fontSize: '14px',
                            outline: 'none',
                            boxSizing: 'border-box'
                        }}
                    />
                </div>

                {/* Epochs */}
                <div className="training-option">
                    <label style={{
                        display: 'block',
                        marginBottom: '6px',
                        color: '#b0b8c4',
                        fontSize: '14px',
                        fontWeight: 500
                    }}>Epochs</label>
                    <input
                        type="number"
                        value={trainingConfig.epochs}
                        onChange={(e) => handleConfigChange('epochs', Number(e.target.value))}
                        min="1"
                        style={{
                            width: '100%',
                            padding: '8px 10px',
                            background: 'rgba(255, 255, 255, 0.07)',
                            border: '1px solid rgba(255, 255, 255, 0.1)',
                            borderRadius: '8px',
                            color: 'white',
                            fontSize: '14px',
                            outline: 'none',
                            boxSizing: 'border-box'
                        }}
                    />
                </div>

                {/* Learning Rate */}
                <div className="training-option">
                    <label style={{
                        display: 'block',
                        marginBottom: '6px',
                        color: '#b0b8c4',
                        fontSize: '14px',
                        fontWeight: 500
                    }}>Learning Rate</label>
                    <input
                        type="number"
                        value={trainingConfig.learningRate}
                        onChange={(e) => handleConfigChange('learningRate', Number(e.target.value))}
                        min="0.0001"
                        max="1"
                        step="0.0001"
                        style={{
                            width: '100%',
                            padding: '8px 10px',
                            background: 'rgba(255, 255, 255, 0.07)',
                            border: '1px solid rgba(255, 255, 255, 0.1)',
                            borderRadius: '8px',
                            color: 'white',
                            fontSize: '14px',
                            outline: 'none',
                            boxSizing: 'border-box'
                        }}
                    />
                </div>

                {/* Optimizer */}
                <div className="training-option">
                    <label style={{
                        display: 'block',
                        marginBottom: '6px',
                        color: '#b0b8c4',
                        fontSize: '14px',
                        fontWeight: 500
                    }}>Optimizer</label>
                    <select
                        value={trainingConfig.optimizer}
                        onChange={(e) => handleConfigChange('optimizer', e.target.value)}
                        style={{
                            width: '100%',
                            padding: '8px 10px',
                            background: 'rgba(255, 255, 255, 0.07)',
                            border: '1px solid rgba(255, 255, 255, 0.1)',
                            borderRadius: '8px',
                            color: 'white',
                            fontSize: '14px',
                            outline: 'none'
                        }}
                    >
                        <option value="adam">Adam</option>
                        <option value="sgd">SGD</option>
                        <option value="rmsprop">RMSprop</option>
                        <option value="adagrad">Adagrad</option>
                    </select>
                </div>

                {/* Loss Function */}
                <div className="training-option">
                    <label style={{
                        display: 'block',
                        marginBottom: '6px',
                        color: '#b0b8c4',
                        fontSize: '14px',
                        fontWeight: 500
                    }}>Loss Function</label>
                    <select
                        value={trainingConfig.lossFunction}
                        onChange={(e) => handleConfigChange('lossFunction', e.target.value)}
                        style={{
                            width: '100%',
                            padding: '8px 10px',
                            background: 'rgba(255, 255, 255, 0.07)',
                            border: '1px solid rgba(255, 255, 255, 0.1)',
                            borderRadius: '8px',
                            color: 'white',
                            fontSize: '14px',
                            outline: 'none'
                        }}
                    >
                        <option value="mse">Mean Squared Error</option>
                        <option value="categorical_crossentropy">Categorical Crossentropy</option>
                        <option value="binary_crossentropy">Binary Crossentropy</option>
                        <option value="sparse_categorical_crossentropy">Sparse Categorical Crossentropy</option>
                    </select>
                </div>

                {/* Validation Split */}
                <div className="training-option">
                    <label style={{
                        display: 'block',
                        marginBottom: '6px',
                        color: '#b0b8c4',
                        fontSize: '14px',
                        fontWeight: 500
                    }}>Validation Split</label>
                    <input
                        type="number"
                        value={trainingConfig.validationSplit}
                        onChange={(e) => handleConfigChange('validationSplit', Number(e.target.value))}
                        min="0"
                        max="0.5"
                        step="0.01"
                        style={{
                            width: '100%',
                            padding: '8px 10px',
                            background: 'rgba(255, 255, 255, 0.07)',
                            border: '1px solid rgba(255, 255, 255, 0.1)',
                            borderRadius: '8px',
                            color: 'white',
                            fontSize: '14px',
                            outline: 'none',
                            boxSizing: 'border-box'
                        }}
                    />
                </div>

                {/* Save Button */}
                <button
                    onClick={handleApply}
                    style={{
                        marginTop: '10px',
                        padding: '10px 16px',
                        background: 'linear-gradient(135deg, #4776E6 0%, #8E54E9 100%)',
                        border: 'none',
                        borderRadius: '8px',
                        color: 'white',
                        fontWeight: 600,
                        cursor: 'pointer',
                        transition: 'all 0.3s ease',
                        boxShadow: '0 2px 10px rgba(71, 118, 230, 0.3)',
                    }}
                    onMouseOver={(e) => {
                        e.currentTarget.style.transform = 'translateY(-2px)';
                        e.currentTarget.style.boxShadow = '0 4px 15px rgba(71, 118, 230, 0.4)';
                    }}
                    onMouseOut={(e) => {
                        e.currentTarget.style.transform = 'translateY(0)';
                        e.currentTarget.style.boxShadow = '0 2px 10px rgba(71, 118, 230, 0.3)';
                    }}
                >
                    Apply Settings
                </button>

                {/* Success message */}
                <div 
                    id="save-success"
                    style={{
                        marginTop: '8px',
                        padding: '8px 12px',
                        background: 'linear-gradient(90deg, #00b09b 0%, #96c93d 100%)',
                        borderRadius: '6px',
                        color: 'white',
                        fontSize: '13px',
                        textAlign: 'center',
                        opacity: 0,
                        transition: 'opacity 0.3s ease'
                    }}
                >
                    Settings applied!
                </div>
            </div>
        </div>
    );
};

export default PropertiesPanel;
