import React, { useState, useRef } from 'react';
import Canvas from './components/canvas/Canvas';
import LayerLibrary from './components/sidebar/LayerLibrary';
import PropertiesPanel from './components/sidebar/PropertiesPanel';
import Toolbar from './components/controls/Toolbar';

// Overlay for user guide video
const UserGuideOverlay: React.FC<{ onClose: () => void }> = ({ onClose }) => (
    <div style={{
        position: 'fixed',
        top: 0,
        left: 0,
        width: '100vw',
        height: '100vh',
        background: 'rgba(10, 10, 20, 0.92)',
        zIndex: 9999,
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        flexDirection: 'column',
        transition: 'opacity 0.3s',
    }}>
        <div style={{ position: 'absolute', top: 32, right: 32 }}>
            <button
                onClick={onClose}
                style={{
                    background: '#222',
                    color: '#fff',
                    border: 'none',
                    borderRadius: 6,
                    fontSize: 22,
                    padding: '8px 18px',
                    cursor: 'pointer',
                    boxShadow: '0 2px 8px rgba(0,0,0,0.2)'
                }}
                aria-label="Close user guide"
            >
                Ã—
            </button>
        </div>
        <div style={{
            background: '#181828',
            borderRadius: 12,
            boxShadow: '0 4px 32px rgba(0,0,0,0.5)',
            padding: 24,
            maxWidth: '90vw',
            maxHeight: '80vh',
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
        }}>
            <h2 style={{ color: '#fff', marginBottom: 16 }}>Welcome to Autai!</h2>
            <p style={{ color: '#ccc', marginBottom: 16, textAlign: 'center', maxWidth: 500 }}>
                Watch this quick tutorial to get started with the neural network builder.
            </p>
            <video
                src={process.env.PUBLIC_URL + '/media/Tutorial_1.mp4'}
                controls
                style={{
                    width: 'min(700px, 80vw)',
                    maxHeight: '60vh',
                    borderRadius: 8,
                    background: '#000',
                    marginBottom: 8
                }}
            />
        </div>
    </div>
);

interface TrainingOptions {
    batchSize: number;
    epochs: number;
    learningRate: number;
    optimizer: string;
    lossFunction: string;
}

const App: React.FC = () => {
    // Add state to track export status
    const [exportStatus, setExportStatus] = useState<{
        isExporting: boolean;
        message: string;
        isError: boolean;
    }>({
        isExporting: false,
        message: '',
        isError: false
    });

    // State for training options
    const [trainingOptions, setTrainingOptions] = useState<TrainingOptions>({
        batchSize: 32,
        epochs: 10,
        learningRate: 0.001,
        optimizer: 'adam',
        lossFunction: 'mse',
    });

    // Handler to update training options
    const handleOptionChange = (option: string, value: any) => {
        setTrainingOptions(prev => ({ ...prev, [option]: value }));
    };

    // Reference to Canvas component to get model data
    const canvasRef = useRef<any>(null);

    // Function to handle export
    const handleExport = async () => {
        if (!canvasRef.current) {
            setExportStatus({
                isExporting: false,
                message: 'Canvas reference not available',
                isError: true
            });
            return;
        }

        try {
            setExportStatus({
                isExporting: true,
                message: 'Exporting model...',
                isError: false
            });

        // Show user guide overlay on first load
        const [showGuide, setShowGuide] = useState(true);

            // Get model data from Canvas component
            const modelData = canvasRef.current.getModelData();

            // Combine model data with training options
            const exportData = {
                ...modelData,
                trainingOptions: trainingOptions
            };

            // Build endpoint list safely for tunnel/https
            const isHttps = typeof window !== 'undefined' && window.location.protocol === 'https:';
            // Read meta without TS assertions/optional chaining
            const metaTag = document.querySelector('meta[name="autai:api-base"]');
            const metaApiBase = metaTag ? (metaTag.getAttribute('content') || '').trim() : '';
            const apiBaseRaw = (process.env.REACT_APP_API_BASE || metaApiBase || '');
            const apiBase = apiBaseRaw.replace(/\/+$/, '');
            const sameOrigin = (typeof window !== 'undefined' ? window.location.origin : '').replace(/\/+$/, '');

            const candidates = [
                `${sameOrigin}/api/export-model`,
                `${sameOrigin}/export-model`,
                apiBase ? `${apiBase}/api/export-model` : '',
                apiBase ? `${apiBase}/export-model` : '',
                !isHttps ? 'http://localhost:5000/api/export-model' : ''
            ].filter(Boolean);

            // De-duplicate while preserving order
            const seen = new Set<string>();
            const endpoints = candidates.filter(u => (seen.has(u) ? false : (seen.add(u), true)));

            let result: any = null;
            let lastError: any = null;

            for (const url of endpoints) {
                try {
                    // Add a timeout to the fetch request
                    const controller = new AbortController();
                    const timeoutId = setTimeout(() => controller.abort(), 5000);

                    const response = await fetch(url, {
                        method: 'POST',
                        mode: 'cors',
                        cache: 'no-store',
                        headers: { 'Content-Type': 'application/json' },
                        body: JSON.stringify(exportData),
                        signal: controller.signal
                    });

                    clearTimeout(timeoutId);

                    const contentType = response.headers.get('content-type') || '';
                    const isJson = contentType.includes('application/json');
                    const payload = isJson ? await response.json() : await response.text();

                    if (!response.ok) {
                        const msg = typeof payload === 'string' ? payload.slice(0, 200) : JSON.stringify(payload);
                        throw new Error(`HTTP ${response.status} from ${url} - ${msg}`);
                    }

                    if (!isJson) {
                        // Likely got HTML (e.g., static host), try next candidate
                        throw new Error(`Non-JSON response from ${url}: ${String(payload).slice(0, 120)}`);
                    }

                    result = payload;
                    break;
                } catch (err) {
                    if (err instanceof Error && err.name === 'AbortError') {
                        lastError = new Error(`Request to ${url} timed out`);
                    } else {
                        lastError = err;
                    }
                    continue;
                }
            }

            if (!result) {
                throw lastError || new Error('Export failed: no valid response from any endpoint');
            }

            if (!result.success) {
                throw new Error(result.message || 'Export failed');
            }

            // Create a downloadable file from the Python code
            const blob = new Blob([result.message], { type: 'text/plain' });
            const url = URL.createObjectURL(blob);

            // Create a temporary link element to trigger download
            const a = document.createElement('a');
            a.href = url;
            a.download = 'autai_model.py';
            document.body.appendChild(a);
            a.click();

            // Clean up
            document.body.removeChild(a);
            URL.revokeObjectURL(url);

            setExportStatus({
                isExporting: false,
                message: 'Export successful! File downloaded.',
                isError: false
            });

            // Clear status message after 3 seconds
            setTimeout(() => {
                setExportStatus(prev => ({
                    ...prev,
                    message: ''
                }));
            }, 3000);

        } catch (error) {
            console.error('Export failed:', error);
            setExportStatus({
                isExporting: false,
                message: `Export failed: ${error instanceof Error ? error.message : 'Unknown error'}`,
                isError: true
            });
        }
    };

    return (
        <div className="app" style={{ 
            height: '100vh', 
            display: 'flex', 
            flexDirection: 'column',
            background: 'linear-gradient(135deg, #0a0a0f 0%, #12121a 100%)',
            color: '#ffffff'
        }}>
            <Toolbar onExport={handleExport} />

            {/* Status message */}
            {exportStatus.message && (
                <div style={{
                    padding: '10px 16px',
                    background: exportStatus.isError 
                        ? 'linear-gradient(90deg, #7a2a2a 0%, #5a1a1a 100%)' 
                        : 'linear-gradient(90deg, #1a4d4d 0%, #2a5a2a 100%)',
                    color: 'white',
                    textAlign: 'center',
                    opacity: exportStatus.message ? 1 : 0,
                    transition: 'opacity 0.3s',
                    fontWeight: '500',
                    boxShadow: '0 2px 10px rgba(0, 0, 0, 0.3)'
                }}>
                    {exportStatus.message}
                </div>
            )}

            <div className="main-container" style={{ 
                flex: 1, 
                display: 'flex', 
                overflow: 'hidden',
                height: 'calc(100vh - 80px)'
            }}>
                <LayerLibrary />
                <div style={{ 
                    flex: 1, 
                    position: 'relative', 
                    overflow: 'hidden',
                    height: '100%'
                }}>
                    <Canvas ref={canvasRef} />
                </div>
                <PropertiesPanel 
                    trainingOptions={trainingOptions}
                    onOptionChange={handleOptionChange}
                />
            </div>
        </div>
    );
};

export default App;
