import React, { useState, useRef, useEffect } from 'react';
import DropZone from './DropZone';
import InputLayer from '../layers/InputLayer';
import Conv2DLayer from '../layers/Conv2DLayer';
import OutputLayer from '../layers/OutputLayer';
import ConcatLayer from '../layers/ConcatLayer';

type LayerType = 'InputLayer' | 'Conv2DLayer' | 'OutputLayer' | 'ConcatLayer';

interface LayerProps {
    [key: string]: any;
}

const layerComponentMap: Record<LayerType, React.ComponentType<LayerProps>> = {
    InputLayer: InputLayer,
    Conv2DLayer: Conv2DLayer,
    OutputLayer: OutputLayer,
    ConcatLayer: ConcatLayer,
};

const defaultParams: Record<LayerType, Record<string, any>> = {
    InputLayer: { inputShape: [28, 28, 1] },
    Conv2DLayer: { filterSize: 3, numFilters: 32, activationFunction: 'relu' },
    OutputLayer: { outputShape: [10], activationFunction: 'softmax' },
    ConcatLayer: { layers: [] },
};

interface DroppedLayer {
    type: LayerType;
    id: number;
    parameters: Record<string, any>;
    x: number;
    y: number;
    selected?: boolean;
}

interface Connection {
    from: number;
    to: number;
}

const Canvas: React.FC = () => {
    const [layers, setLayers] = useState<DroppedLayer[]>([]);
    const [connections, setConnections] = useState<Connection[]>([]);
    const [draggedId, setDraggedId] = useState<number | null>(null);
    const [dragOffset, setDragOffset] = useState<{ x: number; y: number }>({ x: 0, y: 0 });
    const [connecting, setConnecting] = useState<{ from: number | null, to: number | null }>({ from: null, to: null });
    const [selectedLayerId, setSelectedLayerId] = useState<number | null>(null);
    const canvasRef = useRef<HTMLDivElement>(null);

    // Keyboard event handler for deleting selected nodes
    useEffect(() => {
        const handleKeyDown = (e: KeyboardEvent) => {
            // Don't delete if user is typing in an input field
            const activeElement = document.activeElement;
            if (activeElement instanceof HTMLInputElement || 
                activeElement instanceof HTMLTextAreaElement || 
                activeElement instanceof HTMLSelectElement) {
                return;
            }

            if ((e.key === 'Delete' || e.key === 'Backspace') && selectedLayerId !== null) {
                // Remove the selected layer
                setLayers(prev => prev.filter(l => l.id !== selectedLayerId));
                // Remove all connections involving this layer
                setConnections(prev => prev.filter(
                    conn => conn.from !== selectedLayerId && conn.to !== selectedLayerId
                ));
                setSelectedLayerId(null);
                e.preventDefault();
            }
        };

        document.addEventListener('keydown', handleKeyDown);
        return () => document.removeEventListener('keydown', handleKeyDown);
    }, [selectedLayerId]);

    // Add new layer at the mouse drop position
    const handleDrop = (layer: { type: string; id: number; x?: number; y?: number }) => {
        // Only allow valid LayerType
        if (!['InputLayer', 'Conv2DLayer', 'OutputLayer', 'ConcatLayer'].includes(layer.type)) {
            return;
        }

        const validLayerTypes: LayerType[] = ['InputLayer', 'Conv2DLayer', 'OutputLayer', 'ConcatLayer'];
        const type = validLayerTypes.find(t => t === layer.type);

        if (!type) {
            return;
        }

        setLayers(prev => [
            ...prev,
            {
                ...layer,
                type: type,
                parameters: { ...defaultParams[type] },
                x: layer.x ?? 100,
                y: layer.y ?? 100,
            },
        ]);
    };

    // Improved drag logic with input element detection and selection
    const handleMouseDown = (id: number, e: React.MouseEvent) => {
        // Prevent dragging if clicking on input elements, labels, or select elements
        const target = e.target;
        if (target instanceof HTMLInputElement || 
            target instanceof HTMLLabelElement || 
            target instanceof HTMLSelectElement) {
            return;
        }

        // Set selected layer
        setSelectedLayerId(id);

        if (!canvasRef.current) return;

        const layer = layers.find(l => l.id === id);
        if (!layer) return;

        const rect = canvasRef.current.getBoundingClientRect();
        const mouseX = e.clientX - rect.left;
        const mouseY = e.clientY - rect.top;

        // Calculate offset from mouse position to layer's top-left corner
        const offsetX = mouseX - layer.x;
        const offsetY = mouseY - layer.y;

        setDraggedId(id);
        setDragOffset({ x: offsetX, y: offsetY });
        e.stopPropagation();
    };

    // Click outside to deselect
    const handleCanvasClick = (e: React.MouseEvent) => {
        if (e.target === canvasRef.current) {
            setSelectedLayerId(null);
        }
    };

    const handleMouseUp = () => {
        setDraggedId(null);
        setDragOffset({ x: 0, y: 0 });
    };

    const handleMouseMove = (e: React.MouseEvent) => {
        if (draggedId !== null && canvasRef.current) {
            const rect = canvasRef.current.getBoundingClientRect();
            const mouseX = e.clientX - rect.left;
            const mouseY = e.clientY - rect.top;

            // Apply the offset to maintain relative position
            const x = mouseX - dragOffset.x;
            const y = mouseY - dragOffset.y;

            setLayers(prev => prev.map(l => l.id === draggedId ? { ...l, x, y } : l));
        }
    };

    // Inline parameter editing with proper type conversion
    const handleParamChange = (id: number, param: string, value: string) => {
        setLayers(prev => prev.map(l => {
            if (l.id === id) {
                let convertedValue: any = value;

                // Convert numeric strings to numbers
                if (!isNaN(Number(value)) && value.trim() !== '') {
                    convertedValue = Number(value);
                }

                // Handle array inputs (for inputShape, outputShape)
                if (param.includes('Shape') && typeof value === 'string') {
                    try {
                        // Parse comma-separated values as array
                        convertedValue = value.split(',').map(v => Number(v.trim())).filter(v => !isNaN(v));
                    } catch {
                        convertedValue = value;
                    }
                }

                return { ...l, parameters: { ...l.parameters, [param]: convertedValue } };
            }
            return l;
        }));
    };

    // Connection logic with handles
    const handleConnectionStart = (id: number, type: 'input' | 'output', e: React.MouseEvent) => {
        e.stopPropagation();
        if (type === 'output') {
            setConnecting({ from: id, to: null });
        }
    };

    const handleConnectionEnd = (id: number, type: 'input' | 'output', e: React.MouseEvent) => {
        e.stopPropagation();
        if (type === 'input' && connecting.from !== null && connecting.from !== id) {
            // Check if connection already exists
            const existingConnectionIndex = connections.findIndex(
                conn => conn.from === connecting.from && conn.to === id
            );

            if (existingConnectionIndex !== -1) {
                // Disconnect if connection exists
                setConnections(prev => prev.filter((_, index) => index !== existingConnectionIndex));
            } else {
                // Connect if no connection exists
                const fromId = connecting.from;
                setConnections(prev => [...prev, { from: fromId, to: id }]);
            }

            setConnecting({ from: null, to: null });
        }
    };

    // Helper function to get handle positions
    const getHandlePosition = (layer: DroppedLayer, type: 'input' | 'output') => {
        if (type === 'input') {
            return {
                x: layer.x - 8,
                y: layer.y + 50
            };
        } else {
            return {
                x: layer.x + 120 + 8,
                y: layer.y + 50
            };
        }
    };

    // Update connection line positions to connect handles
    const getLayerCenter = (layer: DroppedLayer) => {
        return {
            x: layer.x + 60,
            y: layer.y + 50
        };
    };

    // Updated render connections to use handle positions
    const renderConnections = () => {
        return connections.map((connection, index) => {
            const fromLayer = layers.find(l => l.id === connection.from);
            const toLayer = layers.find(l => l.id === connection.to);

            if (!fromLayer || !toLayer) return null;

            const from = getHandlePosition(fromLayer, 'output');
            const to = getHandlePosition(toLayer, 'input');

            return (
                <svg
                    key={index}
                    style={{
                        position: 'absolute',
                        top: 0,
                        left: 0,
                        width: '100%',
                        height: '100%',
                        pointerEvents: 'none',
                        zIndex: 0
                    }}
                >
                    <line
                        x1={from.x}
                        y1={from.y}
                        x2={to.x}
                        y2={to.y}
                        stroke="#007acc"
                        strokeWidth="2"
                        markerEnd="url(#arrowhead)"
                    />
                    <defs>
                        <marker
                            id="arrowhead"
                            markerWidth="10"
                            markerHeight="7"
                            refX="9"
                            refY="3.5"
                            orient="auto"
                        >
                            <polygon
                                points="0 0, 10 3.5, 0 7"
                                fill="#007acc"
                            />
                        </marker>
                    </defs>
                </svg>
            );
        });
    };

    return (
        <div
            className="canvas"
            ref={canvasRef}
            style={{ 
                minHeight: 500, 
                border: '1px solid #444', 
                position: 'relative', 
                overflow: 'hidden',
                backgroundColor: '#1e1e1e'
            }}
            onMouseMove={handleMouseMove}
            onMouseUp={handleMouseUp}
            onClick={handleCanvasClick}
            tabIndex={0}
        >
            <DropZone onDrop={handleDrop} />

            {/* Render connection lines */}
            {renderConnections()}

            {/* Render blocks */}
            {layers.map((layer) => {
                const LayerComponent = layerComponentMap[layer.type];
                if (!LayerComponent) return null;

                const isSelected = selectedLayerId === layer.id;
                const isConnecting = connecting.from === layer.id;

                // Inline parameter editing UI for demo
                const paramInputs = Object.keys(layer.parameters).map(param => {
                    const value = layer.parameters[param];
                    const displayValue = Array.isArray(value) ? value.join(', ') : String(value);

                    return (
                        <div key={param} style={{ marginBottom: 4 }}>
                            <label style={{ fontSize: 12, color: '#ccc' }}>{param}: </label>
                            <input
                                style={{ 
                                    fontSize: 12, 
                                    width: 80,
                                    backgroundColor: '#3a3a3a',
                                    border: '1px solid #555',
                                    color: '#ffffff',
                                    padding: '2px 4px',
                                    borderRadius: '2px'
                                }}
                                value={displayValue}
                                onChange={e => handleParamChange(layer.id, param, e.target.value)}
                                placeholder={param.includes('Shape') ? "e.g. 28, 28, 1" : ""}
                                onMouseDown={(e) => e.stopPropagation()}
                                onFocus={(e) => e.stopPropagation()}
                            />
                        </div>
                    );
                });

                return (
                    <div key={layer.id}>
                        {/* Input Handle */}
                        <div
                            style={{
                                position: 'absolute',
                                left: layer.x - 8,
                                top: layer.y + 42,
                                width: 16,
                                height: 16,
                                backgroundColor: '#4caf50',
                                borderRadius: '50%',
                                border: '2px solid #ffffff',
                                cursor: 'pointer',
                                zIndex: 3,
                                boxShadow: '0 2px 4px rgba(0, 0, 0, 0.3)'
                            }}
                            onMouseDown={(e) => handleConnectionStart(layer.id, 'input', e)}
                            onMouseUp={(e) => handleConnectionEnd(layer.id, 'input', e)}
                            title="Input - Click to connect from another node's output"
                        />

                        {/* Main Node */}
                        <div
                            style={{
                                position: 'absolute',
                                left: layer.x,
                                top: layer.y,
                                background: isConnecting ? '#1e3a5f' : isSelected ? '#3a3a3a' : '#2d2d2d',
                                border: isConnecting ? '2px solid #007acc' : isSelected ? '2px solid #ff6b6b' : '2px solid #555',
                                borderRadius: 8,
                                padding: 12,
                                minWidth: 120,
                                cursor: 'move',
                                zIndex: draggedId === layer.id ? 2 : 1,
                                color: '#ffffff',
                                boxShadow: isSelected ? '0 0 10px rgba(255, 107, 107, 0.3)' : '0 2px 8px rgba(0, 0, 0, 0.3)',
                                userSelect: 'none',
                                WebkitUserSelect: 'none',
                                MozUserSelect: 'none',
                                msUserSelect: 'none'
                            }}
                            onMouseDown={e => handleMouseDown(layer.id, e)}
                        >
                            <LayerComponent {...layer.parameters} />
                            <div style={{ marginTop: 8, userSelect: 'none' }}>{paramInputs}</div>
                        </div>

                        {/* Output Handle */}
                        <div
                            style={{
                                position: 'absolute',
                                left: layer.x + 120 + 8,
                                top: layer.y + 42,
                                width: 16,
                                height: 16,
                                backgroundColor: '#f44336',
                                borderRadius: '50%',
                                border: '2px solid #ffffff',
                                cursor: 'pointer',
                                zIndex: 3,
                                boxShadow: connecting.from === layer.id ? '0 0 8px rgba(244, 67, 54, 0.8)' : '0 2px 4px rgba(0, 0, 0, 0.3)'
                            }}
                            onMouseDown={(e) => handleConnectionStart(layer.id, 'output', e)}
                            onMouseUp={(e) => handleConnectionEnd(layer.id, 'output', e)}
                            title="Output - Click and drag to connect to another node's input"
                        />

                        {/* Connection indicator (keep existing for visual feedback) */}
                        <div style={{ 
                            position: 'absolute', 
                            top: layer.y - 8, 
                            right: layer.x - 8, 
                            width: 16, 
                            height: 16, 
                            background: '#007acc', 
                            borderRadius: '50%',
                            display: isConnecting ? 'block' : 'none',
                            boxShadow: '0 0 6px rgba(0, 122, 204, 0.5)',
                            zIndex: 2
                        }} />
                    </div>
                );
            })}
        </div>
    );
};

export default Canvas;
